document.addEventListener("DOMContentLoaded",function(){const t=document.querySelectorAll("img.ft");t.forEach(t=>{const e=t.parentElement,i=t.getAttribute("width"),o=t.getAttribute("height"),r=t.getAttribute("alt"),n=t.getAttribute("title");if(!i||""===i.trim()){const i=e.offsetWidth;t.setAttribute("width",i)}if(!o||""===o.trim()){const i=e.offsetHeight;t.setAttribute("height",i)}r&&""!==r.trim()||t.setAttribute("alt","photo"),n&&""!==n.trim()||t.setAttribute("title","photo")})});